<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantPartySecurity\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use Throwable;
use OpenApi\Attributes as OA;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\Gateway\TenantPartySecurityFilter;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\Gateway\TenantPartySecurityCursor;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Domain\TenantPartySecurityAttributes;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\TenantPartySecurity\Application\Usecase\List\TenantPartySecurityListUsecase;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\RelyingParty\Domain\RelyingPartyRef;

class TenantPartySecurityListController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly TenantPartySecurityListUsecase $listUsecase,
    ) {
    }
    #[OA\Get(
        path: "/api/access/tenant-party-security",
        tags: ["Tenant party security"],
        description:"Create",
        responses: [
        new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/TenantPartySecurityApiListDTO")),
        new OA\Response(response: 422, description: "Constraint error"),
    ]
    )
    ]
    public function list(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("List Tenant party security");
        $span = $this->startSpan("List Tenant party security");
        try {
            $params = $request->getQueryParams();
            $filter = new TenantPartySecurityFilter(
                uids: isset($params['uid']) ? [$params['uid']] : (isset($params['uids']) ? explode(',', $params['uids']) : null),
                search: $params['search'] ?? null,
                tenant: isset($params['tenant']) ? new TenantRef($params['tenant']) : null,
                tenants: isset($params['tenants']) ? explode(",", $params['tenants']) : null,
                relyingParty: isset($params['relying-party']) ? new RelyingPartyRef($params['relying-party']) : null,
                relyingPartys: isset($params['relying-partys']) ? explode(",", $params['relying-partys']) : null,
            );
            $cursor = new TenantPartySecurityCursor(
                limit: (int)($params['limit'] ?? 100),
                sinceUid: $params['since-uid'] ?? null,
            );
            $result = $this->listUsecase->list($filter, $cursor);
            $value = ['items' => array_map(fn ($item) => $this->mapTenantPartySecurity($item), $result->values())];
            if ($nextLink = $this->nextLink($result->cursor(), $params)) {
                $value['next'] = "?{$nextLink}";
            }
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function nextLink(?TenantPartySecurityCursor $value, array $params): ?string
    {
        $this->logDebug("Check generic allow for Tenant party security");
        $span = $this->startSpan("Generic allow for Tenant party security");
        try {
            if (!$value) {
                return null;
            }
            $link = [];
            if (isset($params['limit'])) {
                $link['limit'] = 'limit=' . $params['limit'];
            }
            if ($value && $value->sinceUid()) {
                $link['since-uid'] = 'since-uid=' . urlencode($value->sinceUid());
            }
            return implode('&', $link);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapTenantPartySecurity(TenantPartySecurityAttributes $value): TenantPartySecurityApiDTO
    {
        $this->logDebug("Map entity to output dto for Tenant party security");
        $span = $this->startSpan("Map entity to output dto for Tenant party security");
        try {
            $tenant = $value->getTenant();
            $relyingParty = $value->getRelyingParty();
            $dto = new TenantPartySecurityApiDTO();
            $dto->uid = $value->getUid();
            $dto->tenant = $tenant ? ['$ref' => $tenant->uid()] : null;
            $dto->relyingParty = $relyingParty ? ['$ref' => $relyingParty->uid()] : null;
            $dto->policies = $value->getPolicies();
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
