<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantTermsOfUse\Application\Usecase\Retrieve;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Connector\FileStorage\BinaryContent;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\TenantTermsOfUseRef;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Application\Service\Visibility\TenantTermsOfUseVisibilityService;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\Gateway\TenantTermsOfUseReadGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantTermsOfUseRetrieveUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantTermsOfUseVisibilityService $visibility,
        private readonly TenantTermsOfUseReadGateway $reader
    ) {
    }

    public function allowRetrieve(?TenantTermsOfUseRef $ref = null): Allow
    {
        $this->logDebug("Check allow of retrieve usecase for Tenant terms of use");
        $span = $this->startSpan("Check allow of retrieve usecase for Tenant terms of use");
        try {
            $result = $this->dispacher->dispatch(new TenantTermsOfUseRetrieveAllowDecision(Allow::allowed('retrieve', 'Allowed to retrieve by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function hiddenFields(?TenantTermsOfUseRef $ref = null): array
    {
        $this->logDebug("Check hidden fields usecase for Tenant terms of use");
        $span = $this->startSpan("Check hidden fields usecase for Tenant terms of use");
        try {
            return $this->visibility->fieldsToHide($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieve(string $uid): TenantTermsOfUseRetrieveResult
    {
        $this->logDebug("Run retrieve usecase for Tenant terms of use");
        $span = $this->startSpan("Run retrieve usecase for Tenant terms of use");
        try {
            $ref = new TenantTermsOfUseRef($uid);
            $allow = $this->allowRetrieve($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$result = $this->visibility->retrieveVisible($ref)) {
                throw new NotFoundException($uid);
            }
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new TenantTermsOfUseRetrieveResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveAttached(string $uid): BinaryContent
    {
        $this->logDebug("Run retrieve Attached file usecase for Tenant terms of use");
        $span = $this->startSpan("Run retrieve Attached file usecase for Tenant terms of use");
        try {
            $ref = new TenantTermsOfUseRef($uid);
            $allow = $this->allowRetrieve($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$visible = $this->visibility->retrieveVisible($ref)) {
                throw new NotFoundException($uid);
            }
            if (!$url = $visible->getAttached()) {
                throw new NotFoundException($uid);
            }
            return $this->reader->readAttached($url);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
