<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantTermsOfUse\Application\Usecase\Update;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\TenantTermsOfUseRef;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\TenantTermsOfUseAttributes;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Application\Service\Visibility\TenantTermsOfUseVisibilityService;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\Gateway\TenantTermsOfUseWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantTermsOfUseUpdateUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantTermsOfUseVisibilityService $visibility,
        private readonly TenantTermsOfUseWriteGateway $writer
    ) {
    }

    public function allowUpdate(?TenantTermsOfUseRef $ref = null): Allow
    {
        $this->logDebug("Check allow update usecase for Tenant terms of use");
        $span = $this->startSpan("Check allow update usecase for Tenant terms of use");
        try {
            $result = $this->dispacher->dispatch(new TenantTermsOfUseUpdateAllowProposal(Allow::allowed('update', 'Allowed to update by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fixedFields(?TenantTermsOfUseRef $ref = null): array
    {
        $this->logDebug("Check fixed fields for update usecase for Tenant terms of use");
        $span = $this->startSpan("Check fixed fields for update usecase for Tenant terms of use");
        try {
            return $this->visibility->fieldsToFix($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function update(string $uid, TenantTermsOfUseAttributes $attributes): TenantTermsOfUseAttributes
    {
        $this->logDebug("Run update usecase for Tenant terms of use");
        $span = $this->startSpan("Run update usecase for Tenant terms of use");
        try {
            $ref = new TenantTermsOfUseRef($uid);
            $allow = $this->allowUpdate($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$original = $this->visibility->retrieveVisibleForUpdate($ref)) {
                throw new NotFoundException($uid);
            }
            $input = $this->visibility->copyWithFixed($this->dispacher->dispatch(new TenantTermsOfUseUpdateInputProposal($ref, $attributes))->attributes);
            $modified = $original->replace($input);
            $result = $this->writer->update($original, $modified);
            $output = $this->visibility->copyWithHidden($result->toAttributes());
            return $this->dispacher->dispatch(new TenantTermsOfUseUpdateOuputProposal($output))->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
