<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantTermsOfUse\Application\Usecase\UploadAttached;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Connector\FileStorage\BinaryContent;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Application\Service\Visibility\TenantTermsOfUseVisibilityService;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\Gateway\TenantTermsOfUseWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantTermsOfUseUploadAttachedUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TenantTermsOfUseVisibilityService $visibility,
        private readonly TenantTermsOfUseWriteGateway $writer
    ) {
    }

    public function allowUpload(): Allow
    {
        $this->logDebug("Check allow of temp upload Attached usecase for Tenant terms of use");
        $span = $this->startSpan("Check allow of temp upload Attached usecase for Tenant terms of use");
        try {
            $result = $this->dispacher->dispatch(new TenantTermsOfUseUploadAttachedAllowDecision(Allow::allowed('upload', 'Allowed to upload by default')));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function readTemporal(string $query): BinaryContent
    {
        $this->logDebug("Run retrieve temp upload Attached usecase for Tenant terms of use");
        $span = $this->startSpan("Run retrieve temp upload Attached usecase for Tenant terms of use");
        try {
            $allow = $this->allowUpload();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            return $this->writer->readTemporalAttached($query);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function temporalStore(BinaryContent $binary): string
    {
        $this->logDebug("Run temp upload Attached usecase for Tenant terms of use");
        $span = $this->startSpan("Run temp upload Attached usecase for Tenant terms of use");
        try {
            $allow = $this->allowUpload();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $this->dispacher->dispatch(new TenantTermsOfUseUploadAttachedCheck($binary));
            $input = $this->dispacher->dispatch(new TenantTermsOfUseUploadAttachedEnrich($binary, $binary));
            return $this->writer->temporalStoreAttached($input->getResult());
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
