<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\Gateway;

use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\TenantTermsOfUse;

class TenantTermsOfUseCursor
{
    public function __construct(
        private int $limit,
        private ?string $sinceUid = null,
    ) {
    }
    public function next(TenantTermsOfUse $tenantTermsOfUse, ?int $size): TenantTermsOfUseCursor
    {
        $next = clone $this;
        $next->sinceUid = $tenantTermsOfUse->uid();
        $next->limit = $size ?? $this->limit;
        return $next;
    }
    public function limit(): int
    {
        return $this->limit;
    }
    public function sinceUid(): ?string
    {
        return $this->sinceUid;
    }
}
