<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Civi\Lughauth\Shared\Connector\FileStorage\BinaryContent;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\TenantTermsOfUse;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Infrastructure\Driven\TenantTermsOfUseReadRepositoryAdapter;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\TenantTermsOfUseRef;

class TenantTermsOfUseReadGateway
{
    private TenantTermsOfUseReadRepository $repository;
    public function __construct(ContainerInterface $container, ?TenantTermsOfUseReadRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(TenantTermsOfUseReadRepositoryAdapter::class);
    }
    public function enrich(TenantTermsOfUseRef $ref): ?TenantTermsOfUse
    {
        return $this->repository->enrich($ref);
    }
    public function list(?TenantTermsOfUseFilter $filter = null, ?TenantTermsOfUseCursor $cursor = null): TenantTermsOfUseSlide
    {
        return $this->repository->list($filter, $cursor);
    }
    public function retrieve(TenantTermsOfUseFilter $filter): ?TenantTermsOfUse
    {
        return $this->repository->retrieve($filter);
    }
    public function exists(?TenantTermsOfUseFilter $filter): bool
    {
        return $this->repository->exists($filter);
    }
    public function count(?TenantTermsOfUseFilter $filter = null): int
    {
        return $this->repository->count($filter);
    }
    public function readAttached(string $key): BinaryContent
    {
        return $this->repository->readAttached($key);
    }
    public function findOneByUid(string $uid): ?TenantTermsOfUse
    {
        return $this->repository->findOneByUid($uid);
    }
}
