<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\ValueObject\TenantTermsOfUseTextVO;

trait TenantTermsOfUseTextAccesor
{
    private TenantTermsOfUseTextVO $_text;
    public function getText(): string
    {
        return $this->_text->value();
    }
    public function text(): TenantTermsOfUseTextVO
    {
        return $this->_text;
    }
    public function isTextChanged(?self $original = null): bool
    {
        return $this->_text->value() !== ($original ? $original->_text->value() : null);
    }
    public function withText(TenantTermsOfUseTextVO|string $text): self
    {
        $copy = clone $this;
        $copy->_text = TenantTermsOfUseTextVO::from($text);
        return $copy;
    }
}
