    }
    protected function withAssertedTextRules(self $value, ConstraintFailList $errorsList): void
    {
        if ($value->textAssigned = $this->textAssigned) {
            $value->text = TenantTermsOfUseTextVO::tryFrom($this->text, $errorsList);
