<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\ValueObject;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;

class TenantTermsOfUseAttachedVO
{
    private const EXTERNALS = ['http://', 'https://'];

    public static function fromTemporal(string $key): TenantTermsOfUseAttachedVO
    {
        return self::from('temp-store://' . $key);
    }
    public static function fromStored(string $key): TenantTermsOfUseAttachedVO
    {
        return self::from(self::hasSomePreffix($key, self::EXTERNALS) ? $key : 'store://' . $key);
    }
    public static function tryFromTemporal(string $key, ConstraintFailList $list): TenantTermsOfUseAttachedVO
    {
        return self::tryFrom('temp-store://' . $key, $list);
    }
    public static function tryFromStored(string $key, ConstraintFailList $list): TenantTermsOfUseAttachedVO
    {
        return self::tryFrom(self::hasSomePreffix($key, self::EXTERNALS) ? $key : 'store://' . $key, $list);
    }
    private static function isTemporal(?string $key): bool
    {
        return !!$key && strpos($key, 'temp-store://') === 0;
    }
    private static function isStored(?string $key): bool
    {
        return !!$key && strpos($key, 'store://') === 0;
    }
    private static function hasSomePreffix(string $key, array $prefixs): bool
    {
        foreach ($prefixs as $prefix) {
            if (strpos($key, $prefix) === 0) {
                return true;
            }
        }
        return false;
    }
    public static function empty(): TenantTermsOfUseAttachedVO
    {
        return new TenantTermsOfUseAttachedVO(null);
    }
    public static function from(TenantTermsOfUseAttachedVO|string|null $value): TenantTermsOfUseAttachedVO
    {
        if (is_a($value, TenantTermsOfUseAttachedVO::class)) {
            // If is a ValueObject, its already validated
            return $value;
        } else {
            // If is not a ValueObject, validation is need and exception throw
            $errorsList = new ConstraintFailList();
            return self::tryFrom($value, $errorsList);
        }
    }
    public static function tryFrom(mixed $value, ConstraintFailList $list): ?TenantTermsOfUseAttachedVO
    {
        if (is_a($value, TenantTermsOfUseAttachedVO::class)) {
            // If is a ValueObject, its already validated... nothing to append
            return $value;
        } elseif (!$value) {
            return new TenantTermsOfUseAttachedVO($value);
        } elseif (is_string($value)) {
            return new TenantTermsOfUseAttachedVO($value);
        } else {
            $list->add(new ConstraintFail('wrong_type', ['attached'], [$value], ['string']));
            return null;
        }
    }
    /**
     * private constructor to avoid build a value without all the rule validations.
     */
    private function __construct(
        private readonly ?string $attached
    ) {
        if ($this->attached) {
            $this->validateKey($this->attached);
        }
    }
    public function value(): ?string
    {
        if (self::isTemporal($this->attached)) {
            return substr($this->attached, 13);
        } elseif (self::isStored($this->attached)) {
            return substr($this->attached, 8);
        } else {
            return $this->attached;
        }
    }
    private function validateKey(string $key)
    {
        if (!self::hasSomePreffix($key, array_merge(['temp-store://', 'store://'], self::EXTERNALS))) {
            throw new \InvalidArgumentException($key . ' is not a valid upload key');
        }
    }
}
