<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantTermsOfUse\Infrastructure\Driven;

use Psr\EventDispatcher\EventDispatcherInterface;
use Closure;
use Override;
use Throwable;
use Civi\Lughauth\Shared\Connector\FileStorage\FileStoreKey;
use Civi\Lughauth\Shared\Connector\FileStorage\BinaryContent;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Infrastructure\Connector\FileStorage\PdoFileStorage;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\Gateway\TenantTermsOfUseWriteRepository;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\Gateway\TenantTermsOfUseSlide;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\Gateway\TenantTermsOfUseCursor;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\Gateway\TenantTermsOfUseFilter;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\TenantTermsOfUseRef;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\TenantTermsOfUse;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\EntityChangelog\EntityChangelogService;

class TenantTermsOfUseWriteRepositoryAdapter implements TenantTermsOfUseWriteRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly PdoFileStorage $store,
        private readonly TenantTermsOfUsePdoConnector $conn,
        private readonly EventDispatcherInterface $dispacher,
        private readonly EntityChangelogService $changelog,
    ) {
    }
    #[Override]
    public function resolveForUpdate(TenantTermsOfUseRef $ref): ?TenantTermsOfUse
    {
        return $this->conn->retrieveForUpdate(new TenantTermsOfUseFilter(uids: [ $ref->uid() ]));
    }
    #[Override]
    public function listForUpdate(?TenantTermsOfUseFilter $filter = null, ?TenantTermsOfUseCursor $sort = null): TenantTermsOfUseSlide
    {
        $this->logDebug("Count for Tenant terms of use on adapter ");
        $span = $this->startSpan("Count for Tenant terms of use on adapter");
        try {
            $values = $this->conn->listForUpdate($filter, $sort);
            $last = end($values);
            return new TenantTermsOfUseSlide(function ($slide, $next) use ($filter) {
                return $this->listForUpdate($filter, $next);
            }, new TenantTermsOfUseCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieveForUpdate(TenantTermsOfUseFilter $filter): ?TenantTermsOfUse
    {
        $this->logDebug("Count for Tenant terms of use on adapter ");
        $span = $this->startSpan("Count for Tenant terms of use on adapter");
        try {
            return $this->conn->retrieveForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function existsForUpdate(?TenantTermsOfUseFilter $filter): bool
    {
        $this->logDebug("Count for Tenant terms of use on adapter ");
        $span = $this->startSpan("Count for Tenant terms of use on adapter");
        try {
            return $this->conn->existsForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function countForUpdate(?TenantTermsOfUseFilter $filter = null): int
    {
        $this->logDebug("Count for Tenant terms of use on adapter ");
        $span = $this->startSpan("Count for Tenant terms of use on adapter");
        try {
            return $this->conn->countForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function create(TenantTermsOfUse $entity, ?Closure $verify = null): TenantTermsOfUse
    {
        $this->logDebug("Count for Tenant terms of use on adapter ");
        $span = $this->startSpan("Count for Tenant terms of use on adapter");
        try {
            if ($entity->getAttached()) {
                $entity->commitAttachedWith($this->store);
            }
            $created = $this->conn->create($entity, $verify);
            $this->dispach($entity);
            $this->changelog->recordChange('tenant-terms-of-use', $entity->uid(), $entity->asPublicJson(), []);
            return $created;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function update(TenantTermsOfUseRef $reference, TenantTermsOfUse $entity): TenantTermsOfUse
    {
        $this->logDebug("Count for Tenant terms of use on adapter ");
        $span = $this->startSpan("Count for Tenant terms of use on adapter");
        try {
            $original = $reference instanceof TenantTermsOfUse ? $reference : $this->retrieveForUpdate(new TenantTermsOfUseFilter(uids: [$reference->uid()]));
            $prevAttached = $original->getAttached();
            $currAttached = $entity->getAttached();
            if ($prevAttached != $currAttached) {
                if ($prevAttached) {
                    $this->store->deleteFile(new FileStoreKey($prevAttached));
                }
                if ($currAttached) {
                    $entity->commitAttachedWith($this->store);
                }
            }
            $updated = $this->conn->update($entity);
            $this->dispach($entity);
            $original = ($reference instanceof TenantTermsOfUse) ? $reference : $this->conn->retrieve(new TenantTermsOfUseFilter(uids: [ $reference->uid() ]));
            $this->changelog->recordChange('tenant-terms-of-use', $entity->uid(), $entity->asPublicJson(), $original->asPublicJson());
            return $updated;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function delete(TenantTermsOfUse $entity): bool
    {
        $this->logDebug("Count for Tenant terms of use on adapter ");
        $span = $this->startSpan("Count for Tenant terms of use on adapter");
        try {
            $result = $this->conn->delete($entity);
            $currAttached = $entity->getAttached();
            if ($currAttached) {
                $this->store->deleteFile(new FileStoreKey($currAttached));
            }
            $this->dispach($entity);
            $this->changelog->recordDeletion('tenant-terms-of-use', $entity->uid(), $entity->asPublicJson());
            return $result;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function temporalStoreAttached(BinaryContent $binary): string
    {
        $this->logDebug("Tempora store Attached childs for Tenant terms of use on adapter ");
        $span = $this->startSpan("Tempora store Attached childs for Tenant terms of use on adapter");
        try {
            return $this->store->tempStore($binary)->key;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function readTemporalAttached(string $key): BinaryContent
    {
        $this->logDebug("Read temporal Attached childs for Tenant terms of use on adapter ");
        $span = $this->startSpan("Read temporal Attached childs for Tenant terms of use on adapter");
        try {
            $binary = $this->store->retrieveTempFile(new FileStoreKey($key));
            if ($binary) {
                return $binary;
            } else {
                throw new NotFoundException('');
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function commitAttached(string $key, ?string $previous = null): string
    {
        $this->logDebug("Commit Attached childs for Tenant terms of use on adapter ");
        $span = $this->startSpan("Commit Attached childs for Tenant terms of use on adapter");
        try {
            if ($previous) {
                $this->store->deleteFile(new FileStoreKey($previous));
            }
            return $this->store->commitContent(new FileStoreKey($key))->key;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByUid(string $uid): ?TenantTermsOfUse
    {
        $this->logDebug("Find on by uid for Tenant terms of use on adapter");
        $span = $this->startSpan("Find on by uid for Tenant terms of use on adapter");
        try {
            return $this->conn->retrieveForUpdate(new TenantTermsOfUseFilter(uids: [$uid]));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function dispach(TenantTermsOfUse $entity)
    {
        $this->logDebug("Count for Tenant terms of use on adapter ");
        $span = $this->startSpan("Count for Tenant terms of use on adapter");
        try {
            foreach ($entity->getTheEvents() as $event) {
                $this->dispacher->dispatch($event);
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
