<?php

# @autogenerated
declare(strict_types=1);
namespace Civi\Lughauth\Features\Access\TenantTermsOfUse\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;

use OpenApi\Attributes as OA;
use Throwable;
use DateTime;

use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\LongTask\Runner;
use Civi\Lughauth\Shared\Infrastructure\LongTask\TaskKey;
use Civi\Lughauth\Shared\Infrastructure\Sql\SqlTemplate;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Application\Usecase\Disable\TenantTermsOfUseDisableUsecase;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\Gateway\TenantTermsOfUseFilter;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Infrastructure\Driver\Batch\TenantTermsOfUseTaskDisable;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\ValueObject\TenantTermsOfUseUidVO;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\ValueObject\TenantTermsOfUseTenantVO;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\ValueObject\TenantTermsOfUseTextVO;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\ValueObject\TenantTermsOfUseEnabledVO;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\ValueObject\TenantTermsOfUseAttachedVO;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\ValueObject\TenantTermsOfUseActivationDateVO;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Domain\ValueObject\TenantTermsOfUseVersionVO;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Application\Usecase\Disable\TenantTermsOfUseDisableResult;

class TenantTermsOfUseDisableController
{
  use LoggerAwareTrait;
  use TracerAwareTrait;

  public function __construct(
    private readonly Runner $runner,
    private readonly SqlTemplate $sql,
    private readonly TenantTermsOfUseDisableUsecase $disableUsecase,
    private readonly Context $context,
  ) {
  }
  #[OA\Patch(path: "/api/access/tenants-terms-of-use/{uid}/disable", tags: ["Tenant terms of use"], description: "Update the value",
    parameters: [
      new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
    ],
    responses: [
      new OA\Response(response: 200, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/TenantTermsOfUseApiDTO")),
      new OA\Response(response: 404, description: "Not found"),
    ])
  ]
  public function disable(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
  {
    $this->logDebug("disable for Tenant terms of use");
    $span = $this->startSpan("disable for Tenant terms of use");
    $this->sql->begin();
    try {
      if (!isset($args['uid'])) {
        throw new NotFoundException('-');
      }
      $uid = $args['uid'];
      $result = $this->disableUsecase->disable($uid);
      $this->sql->commit();
      $value = $this->mapTenantTermsOfUse($result);
      $response->getBody()->write(json_encode($value));
      return $response->withStatus(201)
            ->withHeader('Content-Type', 'application/json');
    } catch(Throwable $ex) {
      $span->recordException($ex);
      throw $ex;
    } finally {
      $this->sql->close();
      $span->end();
    }
  }
  public function disableAllForQuery(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
  {
    $this->logDebug("disable all for query Tenant terms of use");
    $span = $this->startSpan("disable all for query Tenant terms of use");
    try {
      $params = $request->getQueryParams();
      $filter = new TenantTermsOfUseFilter(
        uids: isset( $params['uid'] ) ? [$params['uid']] : ( isset($params['uids']) ? explode(',', $params['uids']) : null),
        search: $params['search'] ?? null,
        tenant: isset($params['tenant'])? new TenantRef($params['tenant']): null,
        tenants: isset($params['tenants'])? explode(",", $params['tenants']): null,
      );
      $res = $this->runner->run(TenantTermsOfUseTaskDisable::class, ['filter' => $filter]);
      $response->getBody()->write( json_encode($res) );      return $response;
    } catch(Throwable $ex) {
      $span->recordException($ex);
      throw $ex;
    } finally {
      $span->end();
    }
  }
  public function checkDisableAllForQueryStatus(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
  {
    $this->logDebug("Check disable all for query Tenant terms of use");
    $span = $this->startSpan("Check disable all for query Tenant terms of use");
    try {
      if (!isset($args['uid'])) {
        throw new NotFoundException('-');
      }
      $uid = $args['uid'];
      $info = $this->runner->detail( new TaskKey($uid) );
      if( !$info ) {
        throw new NotFoundException('-');
      }
      $response->getBody()->write(json_encode($info));
      return $response->withStatus(200)
        ->withHeader('Content-Type', 'application/json');
    } catch(Throwable $ex) {
      $span->recordException($ex);
      throw $ex;
    } finally {
      $span->end();
    }
  }
}

  private function mapTenantTermsOfUse(TenantTermsOfUseDisableResult $value): TenantTermsOfUseApiDTO
  {
    $this->logDebug("Map entity to output dto for Tenant terms of use");
    $span = $this->startSpan("Map entity to output dto for Tenant terms of use");
    try {
      $tenant = $value->getTenant();
      $dto = new TenantTermsOfUseApiDTO();
      $dto->uid = $value->getUid();
      $dto->tenant = $tenant ? ['$ref' => $tenant->uid()] : null;
      $dto->text = $value->getText();
      $dto->enabled = $value->getEnabled();
      if ($value->getAttached()) {
        $dto->attached = $this->context->getBaseUrl() . '/api/access/tenants-terms-of-use/' . $value->getUid() . '/attached';
      }
      $dto->activationDate = $value->getActivationDate()?->format(DateTime::ATOM);
      $dto->version = $value->getVersion();
      return $dto;
    } catch(Throwable $ex) {
      $span->recordException($ex);
      throw $ex;
    } finally {
      $span->end();
    }
  }
