<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantTermsOfUse\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use DateTime;
use Civi\Lughauth\Shared\Value\StreamResource;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Application\Usecase\Retrieve\TenantTermsOfUseRetrieveUsecase;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Application\Usecase\Retrieve\TenantTermsOfUseRetrieveResult;

class TenantTermsOfUseRetrieveController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly TenantTermsOfUseRetrieveUsecase $retrieveUsecase,
        private readonly Context $context,
    ) {
    }
    #[OA\Get(
        path: "/api/access/tenants-terms-of-use{uid}",
        tags: ["Tenant terms of use"],
        description:"Create",
        parameters: [
               new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
          ],
        responses: [
              new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/TenantTermsOfUseApiDTO")),
              new OA\Response(response: 404, description: "Not found"),
          ]
    )]
    public function retrieve(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Retrieve Tenant terms of use");
        $span = $this->startSpan("Retrieve Tenant terms of use");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $result = $this->retrieveUsecase->retrieve($uid);
            $value = $this->mapTenantTermsOfUse($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveAttached(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Retrieve file Attached Tenant terms of use");
        $span = $this->startSpan("Retrieve file Attached Tenant terms of use");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $result = $this->retrieveUsecase->retrieveAttached($uid);
            $resource = $result->stream;
            fseek($resource, 0, SEEK_END);
            $fileSize = ftell($resource);
            rewind($resource);
            $response = $response->withHeader('Content-Type', 'application/octet-stream')
                               ->withHeader('Content-Disposition', 'attachment; filename="' . $result->name . '"')
                               ->withHeader('Content-Length', (string)$fileSize);
            $stream = new StreamResource($resource);
            return $response->withBody($stream);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapTenantTermsOfUse(TenantTermsOfUseRetrieveResult $value): TenantTermsOfUseApiDTO
    {
        $this->logDebug("Map entity to output dto for Tenant terms of use");
        $span = $this->startSpan("Map entity to output dto for Tenant terms of use");
        try {
            $tenant = $value->getTenant();
            $dto = new TenantTermsOfUseApiDTO();
            $dto->uid = $value->getUid();
            $dto->tenant = $tenant ? ['$ref' => $tenant->uid()] : null;
            $dto->text = $value->getText();
            $dto->enabled = $value->isEnabled();
            if ($value->getAttached()) {
                $dto->attached = $this->context->getBaseUrl() . '/api/access/tenants-terms-of-use/' . $value->getUid() . '/attached';
            }
            $dto->activationDate = $value->getActivationDate()?->format(DateTime::ATOM);
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
