<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TenantTermsOfUse\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use Throwable;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Value\StreamResource;
use Civi\Lughauth\Shared\Value\UploadBinaryContent;
use Civi\Lughauth\Features\Access\TenantTermsOfUse\Application\Usecase\UploadAttached\TenantTermsOfUseUploadAttachedUsecase;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TenantTermsOfUseTempAttachedUploadController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly TenantTermsOfUseUploadAttachedUsecase $uploadUsecase,
        private readonly TenantTermsOfUseRestMapper $mapper,
    ) {
    }
    public function uploadTemp(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Upload temp file for Tenant terms of use");
        $span = $this->startSpan("Upload temp file for Tenant terms of use");
        try {
            $result = $this->uploadUsecase->temporalStore(UploadBinaryContent::fromUpload($request, 'file'));
            $response->getBody()->write($this->mapper->urlForTemporalAttached($result));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'text/plain');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function getTemp(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Retrieve temp file for Tenant terms of use");
        $span = $this->startSpan("Retrieve temp file for Tenant terms of use");
        try {
            $query = $request->getQueryParams();
            if (!isset($query['temp'])) {
                throw new NotFoundException('-');
            }
            $result = $this->uploadUsecase->readTemporal(base64_decode($query['temp']));
            $resource = $result->stream;
            fseek($resource, 0, SEEK_END);
            $fileSize = ftell($resource);
            rewind($resource);
            $response = $response->withHeader('Content-Type', 'application/octet-stream')
                               ->withHeader('Content-Disposition', 'attachment; filename="' . $result->name . '"')
                               ->withHeader('Content-Length', (string)$fileSize);
            $stream = new StreamResource($resource);
            return $response->withBody($stream);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
