<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TrustedClient\Application\Policy\Allow\Create;

use Override;
use Throwable;
use Civi\Lughauth\Features\Access\TrustedClient\Application\Usecase\Create\TrustedClientCreateAllowDecision;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class IsAutenticatedCreateAllow
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(private readonly Context $context)
    {
    }
    public function __invoke(TrustedClientCreateAllowDecision $proposal): TrustedClientCreateAllowDecision
    {
        $this->logDebug("Check IsAutenticatedCreateAllow Trusted client");
        $span = $this->startSpan("Check IsAutenticatedCreateAllow Trusted client");
        try {
            $userContext = $this->context->getIdentity();
            if ($proposal->allow->allowed) {
                $belongs = !$userContext->anonimous;
                $allowed = $belongs ? Allow::allowed('create', 'Enabled for user IsAutenticatedCreate') : Allow::disallowed('create', 'Disabled if not IsAutenticatedCreate');
                $proposal->allow = $allowed;
            }
            return $proposal;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
