<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TrustedClient\Application\Policy\Allow\Update;

use Throwable;
use Civi\Lughauth\Features\Access\TrustedClient\Application\Usecase\Update\TrustedClientUpdateAllowDecision;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class UpdateTrustedClientOnlyForRootAllow
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(private readonly Context $context)
    {
    }
    public function __invoke(TrustedClientUpdateAllowDecision $proposal): TrustedClientUpdateAllowDecision
    {
        $this->logDebug("Check UpdateTrustedClientOnlyForRootAllow Trusted client");
        $span = $this->startSpan("Check UpdateTrustedClientOnlyForRootAllow Trusted client");
        try {
            $userContext = $this->context->getIdentity();
            if ($proposal->isAllowed()) {
                if (! $userContext->hasAnyRole('root:admin')) {
                    $proposal->deny('Disabled if not UpdateTrustedClientOnlyForRoot');
                }
            }
            return $proposal;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
