<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TrustedClient\Application\Service\Visibility;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\ValueObject\TrustedClientAllowedRedirectsListRef;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\TrustedClientRef;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\TrustedClientAttributes;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\TrustedClient;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway\TrustedClientFilter;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway\TrustedClientCursor;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway\TrustedClientReadGateway;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway\TrustedClientSlide;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway\TrustedClientWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TrustedClientVisibilityService
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TrustedClientReadGateway $readGateway,
        private readonly TrustedClientWriteGateway $writeGateway
    ) {
    }

    public function prepareVisibleDataInList(TrustedClient $content): TrustedClientAttributes
    {
        return $this->prepareVisibleDataCallback($content, true);
    }
    public function prepareVisibleData(TrustedClient $content): TrustedClientAttributes
    {
        return $this->prepareVisibleDataCallback($content, false);
    }
    public function copyWithFixed(TrustedClientAttributes $attributes, ?TrustedClient $original = null): TrustedClientAttributes
    {
        $this->logDebug("Copy with fixed for Trusted client");
        $span = $this->startSpan("Copy with fixed for  Trusted client");
        try {
            $visible = $this->visiblesReferences($attributes);
            $fixed = $this->fieldsToFix($original);
            foreach ($fixed as $field) {
                $visible->unset($field);
            }
            $result = $this->dispacher->dispatch(new TrustedClientPresetProposal($attributes, $original));
            return $result->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function copyWithHidden(TrustedClientAttributes $attributes, ?TrustedClient $original = null): TrustedClientAttributes
    {
        $this->logDebug("Copy with hidden for Trusted client");
        $span = $this->startSpan("Copy with hidden for  Trusted client");
        try {
            $hidden = $this->fieldsToHide($original);
            foreach ($hidden as $field) {
                $attributes->unset($field);
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function checkVisibility(\Iterator|string|TrustedClientRef $value): bool
    {
        $this->logDebug("Check visibility of an iterator for Trusted client");
        $span = $this->startSpan("Check visibility of an iterator for  Trusted client");
        try {
            if (is_a($value, \Iterator::class)) {
                $ids = [];
                foreach ($value as $val) {
                    $ids[] = $val->uid();
                }
                $filter = new TrustedClientFilter(uids: $ids);
                return count($ids) == $this->countVisibles($filter);
            } elseif (is_a($value, TrustedClientRef::class)) {
                return !!$this->retrieveVisibleForUpdate($value);
            } else {
                return !!$this->retrieveVisibleForUpdate(new TrustedClientRef($value));
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisibles(TrustedClientFilter $filter, ?TrustedClientCursor $cursor = null): TrustedClientSlide
    {
        $this->logDebug("List visibles for Trusted client");
        $span = $this->startSpan("List visibles for  Trusted client");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->readGateway->list($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisibles(TrustedClientFilter $filter): int
    {
        $this->logDebug("Count visibles for Trusted client");
        $span = $this->startSpan("Count visibles for  Trusted client");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->readGateway->count($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisiblesForUpdate(TrustedClientFilter $filter, ?TrustedClientCursor $cursor = null): TrustedClientSlide
    {
        $this->logDebug("List visibles for update for Trusted client");
        $span = $this->startSpan("List visibles for update for  Trusted client");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->writeGateway->listForUpdate($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisiblesForUpdate(TrustedClientFilter $filter): int
    {
        $this->logDebug("Count visibles for update for Trusted client");
        $span = $this->startSpan("Count visibles for update for  Trusted client");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->writeGateway->countForUpdate($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisible(TrustedClientRef $ref): ?TrustedClient
    {
        $this->logDebug("Retrieve visible for Trusted client");
        $span = $this->startSpan("Retrieve visible for  Trusted client");
        try {
            $filter = new TrustedClientFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->readGateway->retrieve($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisibleForUpdate(TrustedClientRef $ref): ?TrustedClient
    {
        $this->logDebug("Retrieve visible for update for Trusted client");
        $span = $this->startSpan("Retrieve visible for update for  Trusted client");
        try {
            $filter = new TrustedClientFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->writeGateway->retrieveForUpdate($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fieldsToFix(?TrustedClientRef $ref = null): array
    {
        $this->logDebug("Check fields to fix for Trusted client");
        $span = $this->startSpan("Check fields for fix for Trusted client");
        try {
            $result = $this->dispacher->dispatch(new TrustedClientFixedFieldsProposal(TrustedClient::calculatedFields(), $ref));
            return array_merge($this->fieldsToHide($ref), $result->fields);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function fieldsToHide(?TrustedClientRef $ref = null): array
    {
        $this->logDebug("Check fields to hide for Trusted client");
        $span = $this->startSpan("Check fields to hide for  Trusted client");
        try {
            $result = $this->dispacher->dispatch(new TrustedClientHideFieldsProposal([], $ref));
            return $result->fields;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function visiblesReferences(TrustedClientAttributes $attributes): TrustedClientAttributes
    {
        $this->logDebug("Check visibility of parent references for Trusted client");
        $span = $this->startSpan("Check visibility of parent references for  Trusted client");
        try {
            if ($attributes->getAllowedRedirects() && !$this->checkVisibilityForAllowedRedirects($attributes->getAllowedRedirects())) {
                throw new NotFoundException("Unknow AllowedRedirects " . $attributes->getAllowedRedirects()->uid());
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function applyPreVisibilityFilter(TrustedClientFilter $filter)
    {
        $this->logDebug("Compose visibility filter for Trusted client");
        $span = $this->startSpan("Compose visibility filter for  Trusted client");
        try {
            $result = $this->dispacher->dispatch(new TrustedClientFilterProposal($filter));
            return $result->trustedClientFilter;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function checkVisibilityForAllowedRedirects(TrustedClientAllowedRedirectsListRef $items): TrustedClientAllowedRedirectsListRef
    {
        $this->logDebug("Copy with fixed for Trusted client");
        $span = $this->startSpan("Copy with fixed for  Trusted client");
        try {
            foreach ($items as $item) {
            }
            return $items;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function evaluatePostVisibility(TrustedClient $value): bool
    {
        $this->logDebug("Check if item is visible for Trusted client");
        $span = $this->startSpan("Check if item is visible for  Trusted client");
        try {
            $result = $this->dispacher->dispatch(new TrustedClientVisibilityProposal(true, $value));
            return $result->visible;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function prepareVisibleDataCallback(TrustedClient $content, bool $inlist): TrustedClientAttributes
    {
        $this->logDebug("Prepare hidratation to visible data for Trusted client");
        $span = $this->startSpan("Prepare hidratation to visible data for Trusted client");
        try {
            $attributes = $content->toAttributes();
            $result = $this->dispacher->dispatch(new TrustedClientExposeProposal($content, $inlist, $attributes));
            return $result->getAttributes();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
