<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TrustedClient\Application\Usecase\List;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Features\Access\TrustedClient\Application\Service\Visibility\TrustedClientVisibilityService;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway\TrustedClientReadGateway;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway\TrustedClientFilter;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway\TrustedClientCursor;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway\TrustedClientAttributesSlide;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TrustedClientListUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TrustedClientVisibilityService $visibility,
        private readonly TrustedClientReadGateway $reader
    ) {
    }

    public function allowList(): Allow
    {
        $this->logDebug("Check allow of list usecase for Trusted client");
        $span = $this->startSpan("Check allow of list usecase for Trusted client");
        try {
            $result = $this->dispacher->dispatch(new TrustedClientListAllowDecision(Allow::allowed('list', 'Allowed to list by default')));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function count(TrustedClientFilter $filter): int
    {
        $this->logDebug("Run list usecase for Trusted client");
        $span = $this->startSpan("Run list usecase for Trusted client");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $input = $this->dispacher->dispatch(new TrustedClientListInputProposal($filter, null));
            return $this->visibility->countVisibles($input->filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function list(TrustedClientFilter $filter, ?TrustedClientCursor $cursor = null): TrustedClientAttributesSlide
    {
        $this->logDebug("Run list usecase for Trusted client");
        $span = $this->startSpan("Run list usecase for Trusted client");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $input = $this->dispacher->dispatch(new TrustedClientListInputProposal($filter, $cursor));
            $slide = $this->visibility->listVisibles($input->filter, $input->cursor);
            $output = new TrustedClientAttributesSlide($slide->nextCursor(), array_map(fn ($item) => $this->visibility->copyWithHidden($item->toAttributes()), $slide->values()));
            return $this->dispacher->dispatch(new TrustedClientListOutputProposal($output))->slide;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
