<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TrustedClient\Application\Usecase\Retrieve;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\TrustedClientRef;
use Civi\Lughauth\Features\Access\TrustedClient\Application\Service\Visibility\TrustedClientVisibilityService;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway\TrustedClientReadGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TrustedClientRetrieveUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly TrustedClientVisibilityService $visibility,
        private readonly TrustedClientReadGateway $reader
    ) {
    }

    public function allowRetrieve(?TrustedClientRef $ref = null): Allow
    {
        $this->logDebug("Check allow of retrieve usecase for Trusted client");
        $span = $this->startSpan("Check allow of retrieve usecase for Trusted client");
        try {
            $result = $this->dispacher->dispatch(new TrustedClientRetrieveAllowDecision(Allow::allowed('retrieve', 'Allowed to retrieve by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function hiddenFields(?TrustedClientRef $ref = null): array
    {
        $this->logDebug("Check hidden fields usecase for Trusted client");
        $span = $this->startSpan("Check hidden fields usecase for Trusted client");
        try {
            return $this->visibility->fieldsToHide($ref);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieve(string $uid): TrustedClientRetrieveResult
    {
        $this->logDebug("Run retrieve usecase for Trusted client");
        $span = $this->startSpan("Run retrieve usecase for Trusted client");
        try {
            $ref = new TrustedClientRef($uid);
            $allow = $this->allowRetrieve($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$result = $this->visibility->retrieveVisible($ref)) {
                throw new NotFoundException($uid);
            }
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new TrustedClientRetrieveResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
