<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway;

use Civi\Lughauth\Features\Access\TrustedClient\Domain\TrustedClient;

class TrustedClientCursor
{
    public const CODE_ASC = 'code';
    public const CODE_DESC = '-code';

    public function __construct(
        private int $limit,
        private ?string $sinceUid = null,
        private ?string $sinceCode = null,
        private ?array $order = null,
    ) {
    }
    public function next(TrustedClient $trustedClient, ?int $size): TrustedClientCursor
    {
        $next = clone $this;
        $next->sinceUid = $trustedClient->uid();
        $next->sinceCode = $trustedClient->getCode();
        $next->limit = $size ?? $this->limit;
        return $next;
    }
    public function limit(): int
    {
        return $this->limit;
    }
    public function sinceUid(): ?string
    {
        return $this->sinceUid;
    }
    public function sinceCode(): ?string
    {
        return $this->sinceCode;
    }
    public function order(): ?array
    {
        return $this->order;
    }
}
