<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\TrustedClient;
use Civi\Lughauth\Features\Access\TrustedClient\Infrastructure\Driven\TrustedClientReadRepositoryAdapter;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\TrustedClientRef;

class TrustedClientReadGateway
{
    private TrustedClientReadRepository $repository;
    public function __construct(ContainerInterface $container, ?TrustedClientReadRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(TrustedClientReadRepositoryAdapter::class);
    }
    public function enrich(TrustedClientRef $ref): ?TrustedClient
    {
        return $this->repository->enrich($ref);
    }
    public function list(?TrustedClientFilter $filter = null, ?TrustedClientCursor $cursor = null): TrustedClientSlide
    {
        return $this->repository->list($filter, $cursor);
    }
    public function retrieve(TrustedClientFilter $filter): ?TrustedClient
    {
        return $this->repository->retrieve($filter);
    }
    public function exists(?TrustedClientFilter $filter): bool
    {
        return $this->repository->exists($filter);
    }
    public function count(?TrustedClientFilter $filter = null): int
    {
        return $this->repository->count($filter);
    }
    public function findOneByUid(string $uid): ?TrustedClient
    {
        return $this->repository->findOneByUid($uid);
    }
    public function findOneByCode(string $code): ?TrustedClient
    {
        return $this->repository->findOneByCode($code);
    }
}
