<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TrustedClient\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\TrustedClient\Domain\ValueObject\TrustedClientEnabledVO;

trait TrustedClientEnabledAccesor
{
    private TrustedClientEnabledVO $_enabled;
    public function isEnabled(): bool
    {
        return $this->_enabled->value();
    }
    public function enabled(): TrustedClientEnabledVO
    {
        return $this->_enabled;
    }
    public function isEnabledChanged(?self $original = null): bool
    {
        return $this->_enabled->value() !== ($original ? $original->_enabled->value() : null);
    }
    public function withEnabled(TrustedClientEnabledVO|bool $enabled): self
    {
        $copy = clone $this;
        $copy->_enabled = TrustedClientEnabledVO::from($enabled);
        return $copy;
    }
}
