<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TrustedClient\Domain\ValueObject\Holder;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\ValueObject\TrustedClientCodeVO;

trait TrustedClientCodeAttributeHolder
{
    protected TrustedClientCodeVO|string|null $code = null;
    protected bool $codeAssigned = false;

    public function getCodeOrDefault(?TrustedClientCodeVO $code): ?TrustedClientCodeVO
    {
        return $this->codeAssigned ? ($this->code !== null ? TrustedClientCodeVO::from($this->code) : null) : $code;
    }
    public function code(TrustedClientCodeVO|string|null $code): static
    {
        $this->code = $code;
        $this->codeAssigned = true;
        return $this;
    }
    public function getCode(): ?string
    {
        return is_a($this->code, TrustedClientCodeVO::class) ? $this->code->value() : $this->code;
    }
    public function unsetCode(): static
    {
        $this->code = null;
        $this->codeAssigned = false;
        return $this;
    }
    protected function withDefaultCode(): void
    {
    }
    protected function withAssertedCodeRules(self $value, ConstraintFailList $errorsList): void
    {
        if ($value->codeAssigned = $this->codeAssigned) {
            $value->code = TrustedClientCodeVO::tryFrom($this->code, $errorsList);
        }
    }
}
