<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TrustedClient\Domain\ValueObject;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;

class TrustedClientAllowedRedirectsVO
{
    public static function from(TrustedClientAllowedRedirectsVO|TrustedClientAllowedRedirectsListRef| array |null $value): TrustedClientAllowedRedirectsVO
    {
        if (is_a($value, TrustedClientAllowedRedirectsVO::class)) {
            // If is a ValueObject, its already validated
            return $value;
        } else {
            // If is not a ValueObject, validation is need and exception throw
            $errorsList = new ConstraintFailList();
            $candidate = self::tryFrom($value, $errorsList);
            if ($errorsList->hasErrors()) {
                throw $errorsList->asConstraintException();
            }
            return $candidate;
        }
    }
    public static function tryFrom(mixed $value, ConstraintFailList $list): ?TrustedClientAllowedRedirectsVO
    {
        if (is_a($value, TrustedClientAllowedRedirectsVO::class)) {
            // If is a ValueObject, its already validated... nothing to append
            return $value;
        } elseif (is_array($value)) {
            return new TrustedClientAllowedRedirectsVO(TrustedClientAllowedRedirectsListRef::fromArray($value));
        } elseif (!$value) {
            return new TrustedClientAllowedRedirectsVO($value);
        } elseif (is_a($value, TrustedClientAllowedRedirectsListRef::class)) {
            return new TrustedClientAllowedRedirectsVO($value);
        } else {
            $list->add(new ConstraintFail('wrong_type', ['allowedRedirects'], [$value], ['TrustedClientAllowedRedirectsListRef']));
            return null;
        }
    }
    /**
     * private constructor to avoid build a value without all the rule validations.
     */
    private function __construct(
        private readonly ?TrustedClientAllowedRedirectsListRef $allowedRedirects
    ) {
    }
    public function value(): ?TrustedClientAllowedRedirectsListRef
    {
        $value = $this->allowedRedirects;
        if ($value) {
            $value->rewind();
        }
        return $value;
    }
    public function find(string $uid): ?TrustedClientAllowedRedirectsItem
    {
        foreach ($this->allowedRedirects as $item) {
            if ($item->uid() == $uid) {
                return $item;
            }
        }
        return null;
    }
    public function getWithDefaults(TrustedClientAllowedRedirectsVO $defs): ?TrustedClientAllowedRedirectsVO
    {
        $values = [];
        $originals = $defs->value();
        foreach ($this->allowedRedirects as $item) {
            $prev = null;
            foreach ($originals as $original) {
                if ($item->uid() == $original->uid()) {
                    $prev = $original;
                    break;
                }
            }
            $values[] = $prev ? $item->getOrDefault($prev) : $item;
        }
        return TrustedClientAllowedRedirectsVO::from(TrustedClientAllowedRedirectsListRef::fromArray($values));
    }
}
