<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TrustedClient\Infrastructure\Driven;

use Psr\EventDispatcher\EventDispatcherInterface;
use Closure;
use Override;
use Throwable;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway\TrustedClientWriteRepository;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway\TrustedClientSlide;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway\TrustedClientCursor;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway\TrustedClientFilter;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\TrustedClientRef;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\TrustedClient;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\EntityChangelog\EntityChangelogService;

class TrustedClientWriteRepositoryAdapter implements TrustedClientWriteRepository
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly TrustedClientPdoConnector $conn,
        private readonly EventDispatcherInterface $dispacher,
        private readonly EntityChangelogService $changelog,
    ) {
    }
    #[Override]
    public function resolveForUpdate(TrustedClientRef $ref): ?TrustedClient
    {
        return $this->conn->retrieveForUpdate(new TrustedClientFilter(uids: [ $ref->uid() ]));
    }
    #[Override]
    public function listForUpdate(?TrustedClientFilter $filter = null, ?TrustedClientCursor $sort = null): TrustedClientSlide
    {
        $this->logDebug("Count for Trusted client on adapter ");
        $span = $this->startSpan("Count for Trusted client on adapter");
        try {
            $values = $this->conn->listForUpdate($filter, $sort);
            $last = end($values);
            return new TrustedClientSlide(function ($slide, $next) use ($filter) {
                return $this->listForUpdate($filter, $next);
            }, new TrustedClientCursor($sort?->limit ?? 100, $last->uid ?? null), $values);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function retrieveForUpdate(TrustedClientFilter $filter): ?TrustedClient
    {
        $this->logDebug("Count for Trusted client on adapter ");
        $span = $this->startSpan("Count for Trusted client on adapter");
        try {
            return $this->conn->retrieveForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function existsForUpdate(?TrustedClientFilter $filter): bool
    {
        $this->logDebug("Count for Trusted client on adapter ");
        $span = $this->startSpan("Count for Trusted client on adapter");
        try {
            return $this->conn->existsForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function countForUpdate(?TrustedClientFilter $filter = null): int
    {
        $this->logDebug("Count for Trusted client on adapter ");
        $span = $this->startSpan("Count for Trusted client on adapter");
        try {
            return $this->conn->countForUpdate($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function create(TrustedClient $entity, ?Closure $verify = null): TrustedClient
    {
        $this->logDebug("Count for Trusted client on adapter ");
        $span = $this->startSpan("Count for Trusted client on adapter");
        try {
            $created = $this->conn->create($entity, $verify);
            $this->dispach($entity);
            $this->changelog->recordChange('trusted-client', $entity->uid(), $entity->asPublicJson(), []);
            return $created;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function update(TrustedClientRef $reference, TrustedClient $entity): TrustedClient
    {
        $this->logDebug("Count for Trusted client on adapter ");
        $span = $this->startSpan("Count for Trusted client on adapter");
        try {
            $updated = $this->conn->update($entity);
            $this->dispach($entity);
            $original = ($reference instanceof TrustedClient) ? $reference : $this->conn->retrieve(new TrustedClientFilter(uids: [ $ref->uid() ]));
            $this->changelog->recordChange('trusted-client', $entity->uid(), $entity->asPublicJson(), $original->asPublicJson());
            return $updated;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function delete(TrustedClient $entity): bool
    {
        $this->logDebug("Count for Trusted client on adapter ");
        $span = $this->startSpan("Count for Trusted client on adapter");
        try {
            $result = $this->conn->delete($entity);
            $this->dispach($entity);
            $this->changelog->recordDeletion('trusted-client', $entity->uid(), $entity->asPublicJson());
            return $result;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByUid(string $uid): ?TrustedClient
    {
        $this->logDebug("Find on by uid for Trusted client on adapter");
        $span = $this->startSpan("Find on by uid for Trusted client on adapter");
        try {
            return $this->conn->retrieveForUpdate(new TrustedClientFilter(uids: [$uid]));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[Override]
    public function findOneForUpdateByCode(string $code): ?TrustedClient
    {
        $this->logDebug("Find on by code for Trusted client on adapter");
        $span = $this->startSpan("Find on by code for Trusted client on adapter");
        try {
            return $this->conn->retrieveForUpdate(new TrustedClientFilter(code: $code));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function dispach(TrustedClient $entity)
    {
        $this->logDebug("Count for Trusted client on adapter ");
        $span = $this->startSpan("Count for Trusted client on adapter");
        try {
            foreach ($entity->getTheEvents() as $event) {
                $this->dispacher->dispatch($event);
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
