<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TrustedClient\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\TrustedClientRef;
use Civi\Lughauth\Features\Access\TrustedClient\Application\Usecase\Create\TrustedClientCreateUsecase;
use Civi\Lughauth\Features\Access\TrustedClient\Application\Usecase\List\TrustedClientListUsecase;
use Civi\Lughauth\Features\Access\TrustedClient\Application\Usecase\Update\TrustedClientUpdateUsecase;
use Civi\Lughauth\Features\Access\TrustedClient\Application\Usecase\Delete\TrustedClientDeleteUsecase;
use Civi\Lughauth\Features\Access\TrustedClient\Application\Usecase\Retrieve\TrustedClientRetrieveUsecase;
use Civi\Lughauth\Features\Access\TrustedClient\Application\Usecase\Enable\TrustedClientEnableUsecase;
use Civi\Lughauth\Features\Access\TrustedClient\Application\Usecase\Disable\TrustedClientDisableUsecase;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class TrustedClientAllowController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly TrustedClientCreateUsecase $createUsecase,
        private readonly TrustedClientListUsecase $listUsecase,
        private readonly TrustedClientUpdateUsecase $updateUsecase,
        private readonly TrustedClientDeleteUsecase $deleteUsecase,
        private readonly TrustedClientRetrieveUsecase $retrieveUsecase,
        private readonly TrustedClientEnableUsecase $enableUsecase,
        private readonly TrustedClientDisableUsecase $disableUsecase,
    ) {
    }
    #[OA\Get(
        path: "/api/me/acl/access/trusted-clients",
        tags: ["Trusted client access control"],
        description:"Allows",
        responses: [
        new OA\Response(response: 200, description: "Ok"),
    ]
    )
    ]
    public function genericAllow(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Check generic allow for Trusted client");
        $span = $this->startSpan("Generic allow for Trusted client");
        try {
            $value = [
              'allows' => [
                $this->createUsecase->allowCreate(),
                $this->listUsecase->allowList(),
                $this->updateUsecase->allowUpdate(),
              $this->deleteUsecase->allowDelete(),
              $this->retrieveUsecase->allowRetrieve(),
              $this->enableUsecase->allowEnable(),
              $this->disableUsecase->allowDisable(),
              ],
              'fields' => [
                'noEditables' => $this->updateUsecase->fixedFields(),
                'noVisibles' => $this->retrieveUsecase->hiddenFields()
              ]
            ];
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    #[OA\Get(
        path: "/api/me/acl/access/trusted-clients/{uid}",
        tags: ["Trusted client access control"],
        description:"Allows",
        parameters: [
        new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
    ],
        responses: [
        new OA\Response(response: 200, description: "Ok"),
        new OA\Response(response: 404, description: "Not found"),
    ]
    )
    ]
    public function contextualAllow(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Check specific allow for Trusted client");
        $span = $this->startSpan("Specific allow for Trusted client");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $ref = new TrustedClientRef(uid: $args['uid']);
            $value = [
              'allows' => [
                $this->updateUsecase->allowUpdate($ref),
              $this->deleteUsecase->allowDelete($ref),
              $this->retrieveUsecase->allowRetrieve($ref),
              $this->enableUsecase->allowEnable($ref),
              $this->disableUsecase->allowDisable($ref),
              ],
              'fields' => [
                'noEditables' => $this->updateUsecase->fixedFields($ref),
                'noVisibles' => $this->retrieveUsecase->hiddenFields($ref)
              ]
            ];
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
