<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TrustedClient\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\LongTask\Runner;
use Civi\Lughauth\Shared\Infrastructure\LongTask\TaskKey;
use Civi\Lughauth\Shared\Infrastructure\Sql\SqlTemplate;
use Civi\Lughauth\Features\Access\TrustedClient\Application\Usecase\Disable\TrustedClientDisableUsecase;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway\TrustedClientFilter;
use Civi\Lughauth\Features\Access\TrustedClient\Infrastructure\Driver\Batch\TrustedClientTaskDisable;
use Civi\Lughauth\Shared\Security\AesCypherService;
use Civi\Lughauth\Features\Access\TrustedClient\Application\Usecase\Disable\TrustedClientDisableResult;

class TrustedClientDisableController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly Runner $runner,
        private readonly SqlTemplate $sql,
        private readonly TrustedClientDisableUsecase $disableUsecase,
        private readonly AesCypherService $cypherService,
    ) {
    }
    #[OA\Patch(
        path: "/api/access/trusted-clients/{uid}/disable",
        tags: ["Trusted client"],
        description: "Update the value",
        parameters: [
        new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
    ],
        responses: [
        new OA\Response(response: 200, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/TrustedClientApiDTO")),
        new OA\Response(response: 404, description: "Not found"),
    ]
    )
    ]
    public function disable(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("disable for Trusted client");
        $span = $this->startSpan("disable for Trusted client");
        $this->sql->begin();
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $result = $this->disableUsecase->disable($uid);
            $this->sql->commit();
            $value = $this->mapTrustedClient($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(201)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $this->sql->close();
            $span->end();
        }
    }
    public function disableAllForQuery(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("disable all for query Trusted client");
        $span = $this->startSpan("disable all for query Trusted client");
        try {
            $params = $request->getQueryParams();
            $filter = new TrustedClientFilter(
                uids: isset($params['uid']) ? [$params['uid']] : (isset($params['uids']) ? explode(',', $params['uids']) : null),
                search: $params['search'] ?? null,
                code: $params['code'] ?? null,
            );
            $res = $this->runner->run(TrustedClientTaskDisable::class, ['filter' => $filter]);
            $response->getBody()->write(json_encode($res));
            return $response;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function checkDisableAllForQueryStatus(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Check disable all for query Trusted client");
        $span = $this->startSpan("Check disable all for query Trusted client");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $info = $this->runner->detail(new TaskKey($uid));
            if (!$info) {
                throw new NotFoundException('-');
            }
            $response->getBody()->write(json_encode($info));
            return $response->withStatus(200)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapTrustedClient(TrustedClientDisableResult $value): TrustedClientApiDTO
    {
        $this->logDebug("Map entity to output dto for Trusted client");
        $span = $this->startSpan("Map entity to output dto for Trusted client");
        try {
            $dto = new TrustedClientApiDTO();
            $dto->uid = $value->getUid();
            $dto->code = $value->getCode();
            $dto->publicAllow = $value->isPublicAllow();
            $dto->secretOauth = '******';
            $dto->enabled = $value->isEnabled();
            $allowedRedirects = [];
            foreach ($value->getAllowedRedirects() as $item) {
                $allowedRedirects[] = [
                  'uid' => $item->uid(),
                  'url' => $item->getUrl(),
                  'version' => $item->getVersion(),
                 ];
            }
            $dto->allowedRedirects = $allowedRedirects;
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
