<?php

# @autogenerated
declare(strict_types=1);
namespace Civi\Lughauth\Features\Access\TrustedClient\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;

use OpenApi\Attributes as OA;
use Throwable;

use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\LongTask\Runner;
use Civi\Lughauth\Shared\Infrastructure\LongTask\TaskKey;
use Civi\Lughauth\Shared\Infrastructure\Sql\SqlTemplate;
use Civi\Lughauth\Features\Access\TrustedClient\Application\Usecase\Enable\TrustedClientEnableUsecase;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway\TrustedClientFilter;
use Civi\Lughauth\Features\Access\TrustedClient\Infrastructure\Driver\Batch\TrustedClientTaskEnable;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\ValueObject\TrustedClientUidVO;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\ValueObject\TrustedClientCodeVO;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\ValueObject\TrustedClientPublicAllowVO;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\ValueObject\TrustedClientSecretOauthVO;
use Civi\Lughauth\Shared\Security\AesCypherService;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\ValueObject\TrustedClientEnabledVO;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\ValueObject\TrustedClientAllowedRedirectsVO;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\ValueObject\TrustedClientAllowedRedirectsUidVO;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\ValueObject\TrustedClientAllowedRedirectsUrlVO;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\ValueObject\TrustedClientAllowedRedirectsVersionVO;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\ValueObject\TrustedClientAllowedRedirectsItem;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\ValueObject\TrustedClientAllowedRedirectsListRef;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\ValueObject\TrustedClientVersionVO;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\TrustedClient\Application\Usecase\Enable\TrustedClientEnableResult;

class TrustedClientEnableController
{
  use LoggerAwareTrait;
  use TracerAwareTrait;

  public function __construct(
    private readonly Runner $runner,
    private readonly SqlTemplate $sql,
    private readonly TrustedClientEnableUsecase $enableUsecase,
    private readonly AesCypherService $cypherService,
  ) {
  }
  #[OA\Patch(path: "/api/access/trusted-clients/{uid}/enable", tags: ["Trusted client"], description: "Update the value",
    parameters: [
      new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
    ],
    responses: [
      new OA\Response(response: 200, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/TrustedClientApiDTO")),
      new OA\Response(response: 404, description: "Not found"),
    ])
  ]
  public function enable(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
  {
    $this->logDebug("enable for Trusted client");
    $span = $this->startSpan("enable for Trusted client");
    $this->sql->begin();
    try {
      if (!isset($args['uid'])) {
        throw new NotFoundException('-');
      }
      $uid = $args['uid'];
      $result = $this->enableUsecase->enable($uid);
      $this->sql->commit();
      $value = $this->mapTrustedClient($result);
      $response->getBody()->write(json_encode($value));
      return $response->withStatus(201)
            ->withHeader('Content-Type', 'application/json');
    } catch(Throwable $ex) {
      $span->recordException($ex);
      throw $ex;
    } finally {
      $this->sql->close();
      $span->end();
    }
  }
  public function enableAllForQuery(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
  {
    $this->logDebug("enable all for query Trusted client");
    $span = $this->startSpan("enable all for query Trusted client");
    try {
      $params = $request->getQueryParams();
      $filter = new TrustedClientFilter(
        uids: isset( $params['uid'] ) ? [$params['uid']] : ( isset($params['uids']) ? explode(',', $params['uids']) : null),
        search: $params['search'] ?? null,
        code: $params['code'] ?? null,
      );
      $res = $this->runner->run(TrustedClientTaskEnable::class, ['filter' => $filter]);
      $response->getBody()->write( json_encode($res) );      return $response;
    } catch(Throwable $ex) {
      $span->recordException($ex);
      throw $ex;
    } finally {
      $span->end();
    }
  }
  public function checkEnableAllForQueryStatus(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
  {
    $this->logDebug("Check enable all for query Trusted client");
    $span = $this->startSpan("Check enable all for query Trusted client");
    try {
      if (!isset($args['uid'])) {
        throw new NotFoundException('-');
      }
      $uid = $args['uid'];
      $info = $this->runner->detail( new TaskKey($uid) );
      if( !$info ) {
        throw new NotFoundException('-');
      }
      $response->getBody()->write(json_encode($info));
      return $response->withStatus(200)
        ->withHeader('Content-Type', 'application/json');
    } catch(Throwable $ex) {
      $span->recordException($ex);
      throw $ex;
    } finally {
      $span->end();
    }
  }
}

  private function mapTrustedClient(TrustedClientEnableResult $value): TrustedClientApiDTO
  {
    $this->logDebug("Map entity to output dto for Trusted client");
    $span = $this->startSpan("Map entity to output dto for Trusted client");
    try {
      $dto = new TrustedClientApiDTO();
      $dto->uid = $value->getUid();
      $dto->code = $value->getCode();
      $dto->publicAllow = $value->getPublicAllow();
      $dto->secretOauth = '******';
      $dto->enabled = $value->getEnabled();
      $allowedRedirects = [];
      foreach ($value->getAllowedRedirects() as $item) {
        $allowedRedirects[] = [
          'uid' => $item->uid(),
          'url' => $item->getUrl(),
          'version' => $item->getVersion(),
         ];
      }
      $dto->allowedRedirects = $allowedRedirects;
      $dto->version = $value->getVersion();
      return $dto;
    } catch(Throwable $ex) {
      $span->recordException($ex);
      throw $ex;
    } finally {
      $span->end();
    }
  }
