<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\TrustedClient\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use Throwable;
use OpenApi\Attributes as OA;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway\TrustedClientFilter;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\Gateway\TrustedClientCursor;
use Civi\Lughauth\Features\Access\TrustedClient\Domain\TrustedClientAttributes;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Features\Access\TrustedClient\Application\Usecase\List\TrustedClientListUsecase;
use Civi\Lughauth\Shared\Security\AesCypherService;

class TrustedClientListController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly TrustedClientListUsecase $listUsecase,
        private readonly AesCypherService $cypherService,
    ) {
    }
    #[OA\Get(
        path: "/api/access/trusted-clients",
        tags: ["Trusted client"],
        description:"Create",
        responses: [
        new OA\Response(response: 201, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/TrustedClientApiListDTO")),
        new OA\Response(response: 422, description: "Constraint error"),
    ]
    )
    ]
    public function list(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("List Trusted client");
        $span = $this->startSpan("List Trusted client");
        try {
            $params = $request->getQueryParams();
            $filter = new TrustedClientFilter(
                uids: isset($params['uid']) ? [$params['uid']] : (isset($params['uids']) ? explode(',', $params['uids']) : null),
                search: $params['search'] ?? null,
                code: $params['code'] ?? null,
            );
            $cursor = new TrustedClientCursor(
                limit: (int)($params['limit'] ?? 100),
                sinceUid: $params['since-uid'] ?? null,
                sinceCode: $params['since-code'] ?? null,
                order: isset($params['order']) ? $this->extractOrder(explode(',', $params['order'])) : null,
            );
            $result = $this->listUsecase->list($filter, $cursor);
            $value = ['items' => array_map(fn ($item) => $this->mapTrustedClient($item), $result->values())];
            if ($nextLink = $this->nextLink($result->cursor(), $params)) {
                $value['next'] = "?{$nextLink}";
            }
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(200)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function extractOrder(array $order): array
    {
        $this->logDebug("Extract order for Trusted client");
        $span = $this->startSpan("Extract order for Trusted client");
        try {
            $mapped = [];
            foreach ($order as $ord) {
                if ($ord === 'code-asc') {
                    $mapped[] = 'codeAsc';
                }
                if ($ord === 'code-desc') {
                    $mapped[] = 'codeDesc';
                }
            }
            return $mapped;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function nextLink(?TrustedClientCursor $value, array $params): ?string
    {
        $this->logDebug("Check generic allow for Trusted client");
        $span = $this->startSpan("Generic allow for Trusted client");
        try {
            if (!$value) {
                return null;
            }
            $link = [];
            if (isset($params['limit'])) {
                $link['limit'] = 'limit=' . $params['limit'];
            }
            if ($value && $value->sinceUid()) {
                $link['since-uid'] = 'since-uid=' . urlencode($value->sinceUid());
            }
            if ($value && strpos('CODE-ASC', $params['order'] ?? '') !== false && $value->sinceCode()) {
                $link['since-code'] = 'since-code=' . urlencode($value->sinceCode());
            }
            if ($value && strpos('CODE-DESC', $params['order'] ?? '') !== false && $value->sinceCode()) {
                $link['since-code'] = 'since-code=' . urlencode($value->sinceCode());
            }
            return implode('&', $link);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapTrustedClient(TrustedClientAttributes $value): TrustedClientApiDTO
    {
        $this->logDebug("Map entity to output dto for Trusted client");
        $span = $this->startSpan("Map entity to output dto for Trusted client");
        try {
            $dto = new TrustedClientApiDTO();
            $dto->uid = $value->getUid();
            $dto->code = $value->getCode();
            $dto->publicAllow = $value->isPublicAllow();
            $dto->secretOauth = '******';
            $dto->enabled = $value->isEnabled();
            $allowedRedirects = [];
            foreach ($value->getAllowedRedirects() as $item) {
                $allowedRedirects[] = [
                  'uid' => $item->uid(),
                  'url' => $item->getUrl(),
                  'version' => $item->getVersion(),
                 ];
            }
            $dto->allowedRedirects = $allowedRedirects;
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
