<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Application\Policy\Allow\Enable;

use Throwable;
use Civi\Lughauth\Features\Access\User\Application\Usecase\Enable\UserEnableAllowDecision;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class EnableUserOnlyForRootAllow
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(private readonly Context $context)
    {
    }
    public function __invoke(UserEnableAllowDecision $proposal): UserEnableAllowDecision
    {
        $this->logDebug("Check EnableUserOnlyForRootAllow User");
        $span = $this->startSpan("Check EnableUserOnlyForRootAllow User");
        try {
            $userContext = $this->context->getIdentity();
            if ($proposal->isAllowed()) {
                if (! $userContext->hasAnyRole('root:admin')) {
                    $proposal->deny('Disabled if not EnableUserOnlyForRoot');
                }
            }
            return $proposal;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
