<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Application\Policy\Allow\List;

use Override;
use Throwable;
use Civi\Lughauth\Features\Access\User\Application\Usecase\List\UserListAllowProposal;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class ListUserOnlyForRootAllow
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(private readonly Context $context)
    {
    }
    public function __invoke(UserListAllowProposal $proposal): UserListAllowProposal
    {
        $this->logDebug("Check ListUserOnlyForRootAllow User");
        $span = $this->startSpan("Check ListUserOnlyForRootAllow User");
        try {
            $userContext = $this->context->getIdentity();
            if ($proposal->allow->allowed) {
                $belongs = $userContext->hasAnyRole('ROOT');
                $allowed = $belongs ? Allow::allowed('list', 'Enabled for user ListUserOnlyForRoot') : Allow::disallowed('list', 'Disabled if not ListUserOnlyForRoot');
                $proposal->allow = $allowed;
            }
            return $proposal;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
