<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Application\Policy\Fields;

use Throwable;
use Civi\Lughauth\Shared\Context;
use Civi\Lughauth\Features\Access\User\Application\Service\Visibility\UserCollectNonEditableFields;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class FixTenantExcludingRoot
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(private readonly Context $context)
    {
    }
    public function __invoke(UserCollectNonEditableFields $event): UserCollectNonEditableFields
    {
        $this->logDebug("Check FixTenantExcludingRoot User");
        $span = $this->startSpan("Check FixTenantExcludingRoot User");
        try {
            $userContext = $this->context->getIdentity();
            if (!$userContext->hasAnyRole('root:admin')) {
                $event->withAll(['tenant']);
            }
            return $event;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
