<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Application\Service\Visibility;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Features\Access\Tenant\Application\Service\Visibility\TenantVisibilityService;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\User\Domain\UserRef;
use Civi\Lughauth\Features\Access\User\Domain\UserAttributes;
use Civi\Lughauth\Features\Access\User\Domain\User;
use Civi\Lughauth\Features\Access\User\Domain\Gateway\UserFilter;
use Civi\Lughauth\Features\Access\User\Domain\Gateway\UserCursor;
use Civi\Lughauth\Features\Access\User\Domain\Gateway\UserReadGateway;
use Civi\Lughauth\Features\Access\User\Domain\Gateway\UserSlide;
use Civi\Lughauth\Features\Access\User\Domain\Gateway\UserWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class UserVisibilityService
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly UserReadGateway $readGateway,
        private readonly UserWriteGateway $writeGateway,
        private readonly TenantVisibilityService $tenantVisibilityService
    ) {
    }

    public function prepareVisibleDataInList(User $content): UserAttributes
    {
        return $this->prepareVisibleDataCallback($content, true);
    }
    public function prepareVisibleData(User $content): UserAttributes
    {
        return $this->prepareVisibleDataCallback($content, false);
    }
    public function copyWithFixed(UserAttributes $attributes, ?User $original = null): UserAttributes
    {
        $this->logDebug("Copy with fixed for User");
        $span = $this->startSpan("Copy with fixed for  User");
        try {
            $visible = $this->visiblesReferences($attributes);
            $fixed = $this->fieldsToFix($original);
            foreach ($fixed as $field) {
                $visible->unset($field);
            }
            $result = $this->dispacher->dispatch(new UserPresetProposal($attributes, $original));
            return $result->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function copyWithHidden(UserAttributes $attributes, ?User $original = null): UserAttributes
    {
        $this->logDebug("Copy with hidden for User");
        $span = $this->startSpan("Copy with hidden for  User");
        try {
            $hidden = $this->fieldsToHide($original);
            foreach ($hidden as $field) {
                $attributes->unset($field);
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function checkVisibility(\Iterator|string|UserRef $value): bool
    {
        $this->logDebug("Check visibility of an iterator for User");
        $span = $this->startSpan("Check visibility of an iterator for  User");
        try {
            if (is_a($value, \Iterator::class)) {
                $ids = [];
                foreach ($value as $val) {
                    $ids[] = $val->uid();
                }
                $filter = new UserFilter(uids: $ids);
                return count($ids) == $this->countVisibles($filter);
            } elseif (is_a($value, UserRef::class)) {
                return !!$this->retrieveVisibleForUpdate($value);
            } else {
                return !!$this->retrieveVisibleForUpdate(new UserRef($value));
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisibles(UserFilter $filter, ?UserCursor $cursor = null): UserSlide
    {
        $this->logDebug("List visibles for User");
        $span = $this->startSpan("List visibles for  User");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->readGateway->list($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisibles(UserFilter $filter): int
    {
        $this->logDebug("Count visibles for User");
        $span = $this->startSpan("Count visibles for  User");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->readGateway->count($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function listVisiblesForUpdate(UserFilter $filter, ?UserCursor $cursor = null): UserSlide
    {
        $this->logDebug("List visibles for update for User");
        $span = $this->startSpan("List visibles for update for  User");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            $result = $this->writeGateway->listForUpdate($visibleFilter, $cursor);
            return $result->slide(function ($item) {
                return $this->evaluatePostVisibility($item);
            });
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function countVisiblesForUpdate(UserFilter $filter): int
    {
        $this->logDebug("Count visibles for update for User");
        $span = $this->startSpan("Count visibles for update for  User");
        try {
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            return $this->writeGateway->countForUpdate($visibleFilter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisible(UserRef $ref): ?User
    {
        $this->logDebug("Retrieve visible for User");
        $span = $this->startSpan("Retrieve visible for  User");
        try {
            $filter = new UserFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->readGateway->retrieve($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function retrieveVisibleForUpdate(UserRef $ref): ?User
    {
        $this->logDebug("Retrieve visible for update for User");
        $span = $this->startSpan("Retrieve visible for update for  User");
        try {
            $filter = new UserFilter(uids: [$ref->uid()]);
            $visibleFilter = $this->applyPreVisibilityFilter($filter);
            if ($readed = $this->writeGateway->retrieveForUpdate($visibleFilter)) {
                return $this->evaluatePostVisibility($readed) ? $readed : null;
            } else {
                return null;
            }
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function fieldsToFix(?UserRef $ref = null): array
    {
        $this->logDebug("Check fields to fix for User");
        $span = $this->startSpan("Check fields for fix for User");
        try {
            $result = $this->dispacher->dispatch(new UserFixedFieldsProposal(User::calculatedFields(), $ref));
            return array_merge($this->fieldsToHide($ref), $result->fields);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    public function fieldsToHide(?UserRef $ref = null): array
    {
        $this->logDebug("Check fields to hide for User");
        $span = $this->startSpan("Check fields to hide for  User");
        try {
            $result = $this->dispacher->dispatch(new UserHideFieldsProposal([], $ref));
            return $result->fields;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function visiblesReferences(UserAttributes $attributes): UserAttributes
    {
        $this->logDebug("Check visibility of parent references for User");
        $span = $this->startSpan("Check visibility of parent references for  User");
        try {
            if ($attributes->getTenant() && !$this->tenantVisibilityService->checkVisibility($attributes->getTenant())) {
                throw new NotFoundException("Unknow Tenant " . $attributes->getTenant());
            }
            return $attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function applyPreVisibilityFilter(UserFilter $filter)
    {
        $this->logDebug("Compose visibility filter for User");
        $span = $this->startSpan("Compose visibility filter for  User");
        try {
            $result = $this->dispacher->dispatch(new UserFilterProposal($filter));
            return $result->userFilter;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function evaluatePostVisibility(User $value): bool
    {
        $this->logDebug("Check if item is visible for User");
        $span = $this->startSpan("Check if item is visible for  User");
        try {
            $result = $this->dispacher->dispatch(new UserVisibilityProposal(true, $value));
            return $result->visible;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    private function prepareVisibleDataCallback(User $content, bool $inlist): UserAttributes
    {
        $this->logDebug("Prepare hidratation to visible data for User");
        $span = $this->startSpan("Prepare hidratation to visible data for User");
        try {
            $attributes = $content->toAttributes();
            $result = $this->dispacher->dispatch(new UserExposeProposal($content, $inlist, $attributes));
            return $result->getAttributes();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
