<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Application\Usecase\Accept;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\User\Domain\UserRef;
use Civi\Lughauth\Features\Access\User\Application\Service\Visibility\UserVisibilityService;
use Civi\Lughauth\Features\Access\User\Domain\Gateway\UserWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class UserAcceptUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly UserVisibilityService $visibility,
        private readonly UserWriteGateway $writer,
    ) {
    }

    public function allowAccept(?UserRef $ref = null): Allow
    {
        $this->logDebug("Check allow of Accept usecase for User");
        $span = $this->startSpan("Check allow of Accept usecase for User");
        try {
            $result = $this->dispacher->dispatch(new UserAcceptAllowDecision(Allow::allowed('accept', 'Allowed to User by default'), $ref));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function accept(string $uid): UserAcceptResult
    {
        $this->logDebug("Run Accept usecase for User");
        $span = $this->startSpan("Run Accept usecase for User");
        try {
            $ref = new UserRef($uid);
            $allow = $this->allowAccept($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$original = $this->visibility->retrieveVisibleForUpdate($ref)) {
                throw new NotFoundException($uid);
            }
            $this->dispacher->dispatch(new UserAcceptCheck($original));
            $modified = $original->accept();
            $result = $this->writer->update($original, $modified);
            $output = $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($result));
            return new UserAcceptResult($output);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
