<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Application\Usecase\Delete;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\User\Domain\UserRef;
use Civi\Lughauth\Features\Access\User\Application\Service\Visibility\UserVisibilityService;
use Civi\Lughauth\Features\Access\User\Domain\Gateway\UserWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class UserDeleteUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly UserVisibilityService $visibility,
        private readonly UserWriteGateway $writer,
    ) {
    }

    public function allowDelete(?UserRef $ref = null): Allow
    {
        $this->logDebug("Check allow of delete usecase for User");
        $span = $this->startSpan("Check allow of delete usecase for User");
        try {
            $result = $this->dispacher->dispatch(new UserDeleteAllowDecision(Allow::allowed('delete', 'Allowed to delete by default'), $ref));
            return $result->getAllow();
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function delete(string $uid): void
    {
        $this->logDebug("Run delete usecase for User");
        $span = $this->startSpan("Run delete usecase for User");
        try {
            $ref = new UserRef($uid);
            $allow = $this->allowDelete($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            if (!$original = $this->visibility->retrieveVisibleForUpdate($ref)) {
                throw new NotFoundException($uid);
            }
            $this->dispacher->dispatch(new UserDeleteCheck($original));
            $deleted = $original->delete();
            $this->writer->delete($deleted);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
