<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Application\Usecase\Disable;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Features\Access\User\Domain\UserRef;
use Civi\Lughauth\Features\Access\User\Domain\UserAttributes;
use Civi\Lughauth\Features\Access\User\Application\Service\Visibility\UserVisibilityService;
use Civi\Lughauth\Features\Access\User\Domain\Gateway\UserWriteGateway;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class UserDisableUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly UserVisibilityService $visibility,
        private readonly UserWriteGateway $writer
    ) {
    }

    public function allowDisable(?UserRef $ref = null): Allow
    {
        $this->logDebug("Check allow of Disable usecase for User");
        $span = $this->startSpan("Check allow of Disable usecase for User");
        try {
            $result = $this->dispacher->dispatch(new UserDisableAllowProposal(Allow::allowed('disable', 'Allowed to User by default'), $ref));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function disable(string $uid): UserAttributes
    {
        $this->logDebug("Run Disable usecase for User");
        $span = $this->startSpan("Run Disable usecase for User");
        try {
            $ref = new UserRef($uid);
            $allow = $this->allowDisable($ref);
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $input = $this->dispacher->dispatch(new UserDisableInputProposal($ref));
            if (!$original = $this->visibility->retrieveVisibleForUpdate($input->ref)) {
                throw new NotFoundException($uid);
            }
            $modified = $original->disable();
            $result = $this->writer->update($original, $modified);
            $output = $this->visibility->copyWithHidden($result->toAttributes());
            return $this->dispacher->dispatch(new UserDisableOuputProposal($output))->attributes;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
