<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Application\Usecase\List;

use Psr\EventDispatcher\EventDispatcherInterface;
use Throwable;
use Civi\Lughauth\Shared\Security\Allow;
use Civi\Lughauth\Shared\Exception\UnauthorizedException;
use Civi\Lughauth\Features\Access\User\Application\Service\Visibility\UserVisibilityService;
use Civi\Lughauth\Features\Access\User\Domain\Gateway\UserReadGateway;
use Civi\Lughauth\Features\Access\User\Domain\Gateway\UserFilter;
use Civi\Lughauth\Features\Access\User\Domain\Gateway\UserCursor;
use Civi\Lughauth\Features\Access\User\Domain\Gateway\UserAttributesSlide;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;

class UserListUsecase
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly EventDispatcherInterface $dispacher,
        private readonly UserVisibilityService $visibility,
        private readonly UserReadGateway $reader
    ) {
    }

    public function allowList(): Allow
    {
        $this->logDebug("Check allow of list usecase for User");
        $span = $this->startSpan("Check allow of list usecase for User");
        try {
            $result = $this->dispacher->dispatch(new UserListAllowDecision(Allow::allowed('list', 'Allowed to list by default')));
            return $result->allow;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function count(UserFilter $filter): int
    {
        $this->logDebug("Run list usecase for User");
        $span = $this->startSpan("Run list usecase for User");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            return $this->visibility->countVisibles($filter);
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function list(UserFilter $filter, ?UserCursor $cursor = null): UserAttributesSlide
    {
        $this->logDebug("Run list usecase for User");
        $span = $this->startSpan("Run list usecase for User");
        try {
            $allow = $this->allowList();
            if (!$allow->allowed) {
                throw new UnauthorizedException($allow->reason);
            }
            $slide = $this->visibility->listVisibles($filter, $cursor);
            return new UserAttributesSlide($slide->nextCursor(), array_map(fn ($item) => $this->visibility->copyWithHidden($this->visibility->prepareVisibleData($item)), $slide->values()));
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
