<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Domain\Gateway;

use Psr\Container\ContainerInterface;
use Closure;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Features\Access\User\Domain\User;
use Civi\Lughauth\Features\Access\User\Domain\UserRef;
use Civi\Lughauth\Features\Access\User\Infrastructure\Driven\UserWriteRepositoryAdapter;

class UserWriteGateway
{
    private UserWriteRepository $repository;
    public function __construct(ContainerInterface $container, ?UserWriteRepository $repository = null)
    {
        $this->repository = $repository ?? $container->get(UserWriteRepositoryAdapter::class);
    }
    public function create(User $entity, ?Closure $verify = null): User
    {
        return $this->repository->create($entity, $verify);
    }
    public function update(UserRef $ref, User $entity): User
    {
        return $this->repository->update($ref, $entity);
    }
    public function delete(User $entity): bool
    {
        return $this->repository->delete($entity);
    }
    public function listForUpdate(?UserFilter $filter = null, ?UserCursor $cursor = null): UserSlide
    {
        return $this->repository->listForUpdate($filter, $cursor);
    }
    public function retrieveForUpdate(UserFilter $filter): ?User
    {
        return $this->repository->retrieveForUpdate($filter);
    }
    public function existsForUpdate(?UserFilter $filter): bool
    {
        return $this->repository->existsForUpdate($filter);
    }
    public function countForUpdate(?UserFilter $filter = null): int
    {
        return $this->repository->countForUpdate($filter);
    }
    public function findOneForUpdateByUid(string $uid): ?User
    {
        return $this->repository->findOneForUpdateByUid($uid);
    }
    public function findOneForUpdateByTenantAndName(TenantRef $tenant, string $name): ?User
    {
        return $this->repository->findOneForUpdateByTenantAndName($tenant, $name);
    }
}
