<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\User\Domain\ValueObject\UserApproveVO;
use Civi\Lughauth\Features\Access\User\Domain\UserApproveOptions;

trait UserApproveAccesor
{
    private ?UserApproveVO $_approve = null;
    public function getApprove(): ?UserApproveOptions
    {
        return $this->_approve?->value();
    }
    public function approve(): ?UserApproveVO
    {
        return $this->_approve;
    }
    public function isApproveChanged(?self $original = null): bool
    {
        return $this->_approve->value() !== ($original ? $original->_approve->value() : null);
    }
    public function withApprove(UserApproveVO|UserApproveOptions|null $approve): self
    {
        $copy = clone $this;
        $copy->_approve = UserApproveVO::from($approve);
        return $copy;
    }
}
