<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\User\Domain\ValueObject\UserApprovedVO;

trait UserApprovedAccesor
{
    private ?UserApprovedVO $_approved = null;
    public function getApproved(): ?bool
    {
        return $this->_approved?->value();
    }
    public function approved(): ?UserApprovedVO
    {
        return $this->_approved;
    }
    public function isApprovedChanged(?self $original = null): bool
    {
        return $this->_approved->value() !== ($original ? $original->_approved->value() : null);
    }
    public function withApproved(UserApprovedVO|bool|null $approved): self
    {
        $copy = clone $this;
        $copy->_approved = UserApprovedVO::from($approved);
        return $copy;
    }
}
