<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\User\Domain\ValueObject\UserEmailVO;

trait UserEmailAccesor
{
    private ?UserEmailVO $_email = null;
    public function getEmail(): ?string
    {
        return $this->_email?->value();
    }
    public function email(): ?UserEmailVO
    {
        return $this->_email;
    }
    public function isEmailChanged(?self $original = null): bool
    {
        return $this->_email->value() !== ($original ? $original->_email->value() : null);
    }
    public function withEmail(UserEmailVO|string|null $email): self
    {
        $copy = clone $this;
        $copy->_email = UserEmailVO::from($email);
        return $copy;
    }
}
