<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\User\Domain\ValueObject\UserNameVO;

trait UserNameAccesor
{
    private UserNameVO $_name;
    public function getName(): string
    {
        return $this->_name->value();
    }
    public function name(): UserNameVO
    {
        return $this->_name;
    }
    public function isNameChanged(?self $original = null): bool
    {
        return $this->_name->value() !== ($original ? $original->_name->value() : null);
    }
    public function withName(UserNameVO|string $name): self
    {
        $copy = clone $this;
        $copy->_name = UserNameVO::from($name);
        return $copy;
    }
}
