<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\User\Domain\ValueObject\UserRejectedVO;

trait UserRejectedAccesor
{
    private ?UserRejectedVO $_rejected = null;
    public function getRejected(): ?bool
    {
        return $this->_rejected?->value();
    }
    public function rejected(): ?UserRejectedVO
    {
        return $this->_rejected;
    }
    public function isRejectedChanged(?self $original = null): bool
    {
        return $this->_rejected->value() !== ($original ? $original->_rejected->value() : null);
    }
    public function withRejected(UserRejectedVO|bool|null $rejected): self
    {
        $copy = clone $this;
        $copy->_rejected = UserRejectedVO::from($rejected);
        return $copy;
    }
}
