<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\User\Domain\ValueObject\UserTenantVO;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;

trait UserTenantAccesor
{
    private UserTenantVO $_tenant;
    public function getTenant(): TenantRef
    {
        return $this->_tenant->value();
    }
    public function tenant(): UserTenantVO
    {
        return $this->_tenant;
    }
    public function isTenantChanged(?self $original = null): bool
    {
        return $this->_tenant->value() !== ($original ? $original->_tenant->value() : null);
    }
    public function withTenant(UserTenantVO|TenantRef $tenant): self
    {
        $copy = clone $this;
        $copy->_tenant = UserTenantVO::from($tenant);
        return $copy;
    }
}
