<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Domain\ValueObject\Accesor;

use Civi\Lughauth\Features\Access\User\Domain\ValueObject\UserVersionVO;

trait UserVersionAccesor
{
    private ?UserVersionVO $_version = null;
    public function getVersion(): ?int
    {
        return $this->_version?->value();
    }
    public function version(): ?UserVersionVO
    {
        return $this->_version;
    }
    public function isVersionChanged(?self $original = null): bool
    {
        return $this->_version->value() !== ($original ? $original->_version->value() : null);
    }
    public function withVersion(UserVersionVO|int|null $version): self
    {
        $copy = clone $this;
        $copy->_version = UserVersionVO::from($version);
        return $copy;
    }
}
