<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Domain\ValueObject\Holder;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\User\Domain\ValueObject\UserEnabledVO;

trait UserEnabledAttributeHolder
{
    protected UserEnabledVO|bool|null $enabled = null;
    protected bool $enabledAssigned = false;

    public function getEnabledOrDefault(?UserEnabledVO $enabled): ?UserEnabledVO
    {
        return $this->enabledAssigned ? ($this->enabled !== null ? UserEnabledVO::from($this->enabled) : null) : $enabled;
    }
    public function enabled(UserEnabledVO|bool|null $enabled): static
    {
        $this->enabled = $enabled;
        $this->enabledAssigned = true;
        return $this;
    }
    public function isEnabled(): ?bool
    {
        return is_a($this->enabled, UserEnabledVO::class) ? $this->enabled->value() : $this->enabled;
    }
    public function unsetEnabled(): static
    {
        $this->enabled = null;
        $this->enabledAssigned = false;
        return $this;
    }
    protected function withDefaultEnabled(): void
    {
        if ($this->enabled === null) {
            $this->enabled = true;
        }
    }
    protected function withAssertedEnabledRules(self $value, ConstraintFailList $errorsList): void
    {
        if ($value->enabledAssigned = $this->enabledAssigned) {
            $value->enabled = UserEnabledVO::tryFrom($this->enabled, $errorsList);
        }
    }
}
