<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Domain\ValueObject\Holder;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Features\Access\User\Domain\ValueObject\UserVersionVO;

trait UserVersionAttributeHolder
{
    protected UserVersionVO|int|null $version = null;
    protected bool $versionAssigned = false;

    public function getVersionOrDefault(?UserVersionVO $version): ?UserVersionVO
    {
        return $this->versionAssigned ? ($this->version !== null ? UserVersionVO::from($this->version) : null) : $version;
    }
    public function version(UserVersionVO|int|null $version): static
    {
        $this->version = $version;
        $this->versionAssigned = true;
        return $this;
    }
    public function getVersion(): ?int
    {
        return is_a($this->version, UserVersionVO::class) ? $this->version->value() : $this->version;
    }
    public function unsetVersion(): static
    {
        $this->version = null;
        $this->versionAssigned = false;
        return $this;
    }
    protected function withDefaultVersion(): void
    {
    }
    protected function withAssertedVersionRules(self $value, ConstraintFailList $errorsList): void
    {
        if ($value->versionAssigned = $this->versionAssigned) {
            $value->version = UserVersionVO::tryFrom($this->version, $errorsList);
        }
    }
}
