<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Domain\ValueObject;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;
use Civi\Lughauth\Shared\Value\Validation\Rule\Email;
use Civi\Lughauth\Shared\Value\Validation\Rule\Length;

class UserEmailVO
{
    public static function empty(): UserEmailVO
    {
        return new UserEmailVO(null);
    }
    public static function from(UserEmailVO|string|null $value): UserEmailVO
    {
        if (is_a($value, UserEmailVO::class)) {
            // If is a ValueObject, its already validated
            return $value;
        } else {
            // If is not a ValueObject, validation is need and exception throw
            $errorsList = new ConstraintFailList();
            $candidate = self::tryFrom($value, $errorsList);
            if ($errorsList->hasErrors()) {
                throw $errorsList->asConstraintException();
            }
            return $candidate;
        }
    }
    public static function tryFrom(mixed $value, ConstraintFailList $list): ?UserEmailVO
    {
        if (is_a($value, UserEmailVO::class)) {
            // If is a ValueObject, its already validated... nothing to append
            return $value;
        } elseif (!$value) {
            return new UserEmailVO($value);
        } elseif (is_string($value)) {
            // If is a ValueObject, we need to append all the errors for the context
            $valid = true;
            foreach (self::rules() as $rule) {
                if ($fail = $rule->check($value)) {
                    $list->add(ConstraintFail::fromRuleFail('email', $fail));
                    $valid = false;
                }
            }
            return $valid ? new UserEmailVO($value) : null;
        } else {
            $list->add(new ConstraintFail('wrong_type', ['email'], [$value], ['string']));
            return null;
        }
    }
    public static function rules(): array
    {
        return [
          new Email(),
          new Length(min: null, max: 250),
        ];
    }
    /**
     * private constructor to avoid build a value without all the rule validations.
     */
    private function __construct(
        private readonly ?string $email
    ) {
    }
    public function value(): ?string
    {
        return $this->email;
    }
}
