<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Domain\ValueObject;

use Civi\Lughauth\Shared\Value\Validation\ConstraintFailList;
use Civi\Lughauth\Shared\Value\Validation\ConstraintFail;

class UserRejectedVO
{
    public static function empty(): UserRejectedVO
    {
        return new UserRejectedVO(null);
    }
    public static function from(UserRejectedVO|bool|null $value): UserRejectedVO
    {
        if (is_a($value, UserRejectedVO::class)) {
            // If is a ValueObject, its already validated
            return $value;
        } else {
            // If is not a ValueObject, validation is need and exception throw
            $errorsList = new ConstraintFailList();
            return self::tryFrom($value, $errorsList);
        }
    }
    public static function tryFrom(mixed $value, ConstraintFailList $list): ?UserRejectedVO
    {
        if (is_a($value, UserRejectedVO::class)) {
            // If is a ValueObject, its already validated... nothing to append
            return $value;
        } elseif (!$value) {
            return new UserRejectedVO($value);
        } elseif (is_bool($value)) {
            return new UserRejectedVO($value);
        } else {
            $list->add(new ConstraintFail('wrong_type', ['rejected'], [$value], ['bool']));
            return null;
        }
    }
    /**
     * private constructor to avoid build a value without all the rule validations.
     */
    private function __construct(
        private readonly ?bool $rejected
    ) {
    }
    public function value(): ?bool
    {
        return $this->rejected;
    }
}
