            $candidate = self::tryFrom($value, $errorsList);
            if ($errorsList->hasErrors()) {
                throw $errorsList->asConstraintException();
            }
            return $candidate;
        }
    }
    public static function tryFrom(mixed $value, ConstraintFailList $list): ?UserTemporalPasswordVO
    {
        if (is_a($value, UserTemporalPasswordVO::class)) {
            // If is a ValueObject, its already validated... nothing to append
            return $value;
        } elseif (!$value) {
            return new UserTemporalPasswordVO($value);
        } elseif (is_bool($value)) {
            return new UserTemporalPasswordVO($value);
        } else {
            $list->add(new ConstraintFail('wrong_type', ['temporalPassword'], [$value], ['bool']));
            return null;
        }
