<?php

# @autogenerated
declare(strict_types=1);

namespace Civi\Lughauth\Features\Access\User\Infrastructure\Driver\Rest;

use Psr\Http\Message\ResponseInterface;
use Psr\Http\Message\ServerRequestInterface;
use OpenApi\Attributes as OA;
use Throwable;
use DateTime;
use Civi\Lughauth\Shared\Exception\NotFoundException;
use Civi\Lughauth\Shared\Observability\LoggerAwareTrait;
use Civi\Lughauth\Shared\Observability\TracerAwareTrait;
use Civi\Lughauth\Shared\Infrastructure\LongTask\Runner;
use Civi\Lughauth\Shared\Infrastructure\LongTask\TaskKey;
use Civi\Lughauth\Shared\Infrastructure\Sql\SqlTemplate;
use Civi\Lughauth\Features\Access\User\Application\Usecase\Accept\UserAcceptUsecase;
use Civi\Lughauth\Features\Access\User\Domain\Gateway\UserFilter;
use Civi\Lughauth\Features\Access\User\Infrastructure\Driver\Batch\UserTaskAccept;
use Civi\Lughauth\Features\Access\Tenant\Domain\TenantRef;
use Civi\Lughauth\Shared\Security\AesCypherService;
use Civi\Lughauth\Features\Access\User\Application\Usecase\Accept\UserAcceptResult;

class UserAcceptController
{
    use LoggerAwareTrait;
    use TracerAwareTrait;

    public function __construct(
        private readonly Runner $runner,
        private readonly SqlTemplate $sql,
        private readonly UserAcceptUsecase $acceptUsecase,
        private readonly AesCypherService $cypherService,
    ) {
    }
    #[OA\Patch(
        path: "/api/access/users/{uid}/accept",
        tags: ["User"],
        description: "Update the value",
        parameters: [
        new OA\PathParameter(name: "uid", required: true, schema: new OA\Schema(type: "string")),
    ],
        responses: [
        new OA\Response(response: 200, description: "Ok", content: new OA\JsonContent(type: "object", ref:"#/components/schemas/UserApiDTO")),
        new OA\Response(response: 404, description: "Not found"),
    ]
    )
    ]
    public function accept(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("accept for User");
        $span = $this->startSpan("accept for User");
        $this->sql->begin();
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $result = $this->acceptUsecase->accept($uid);
            $this->sql->commit();
            $value = $this->mapUser($result);
            $response->getBody()->write(json_encode($value));
            return $response->withStatus(201)
                  ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $this->sql->close();
            $span->end();
        }
    }
    public function acceptAllForQuery(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("accept all for query User");
        $span = $this->startSpan("accept all for query User");
        try {
            $params = $request->getQueryParams();
            $filter = new UserFilter(
                uids: isset($params['uid']) ? [$params['uid']] : (isset($params['uids']) ? explode(',', $params['uids']) : null),
                search: $params['search'] ?? null,
                name: $params['name'] ?? null,
                tenant: isset($params['tenant']) ? new TenantRef($params['tenant']) : null,
                tenants: isset($params['tenants']) ? explode(",", $params['tenants']) : null,
            );
            $res = $this->runner->run(UserTaskAccept::class, ['filter' => $filter]);
            $response->getBody()->write(json_encode($res));
            return $response;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
    public function checkAcceptAllForQueryStatus(ServerRequestInterface $request, ResponseInterface $response, array $args): ResponseInterface
    {
        $this->logDebug("Check accept all for query User");
        $span = $this->startSpan("Check accept all for query User");
        try {
            if (!isset($args['uid'])) {
                throw new NotFoundException('-');
            }
            $uid = $args['uid'];
            $info = $this->runner->detail(new TaskKey($uid));
            if (!$info) {
                throw new NotFoundException('-');
            }
            $response->getBody()->write(json_encode($info));
            return $response->withStatus(200)
              ->withHeader('Content-Type', 'application/json');
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }

    private function mapUser(UserAcceptResult $value): UserApiDTO
    {
        $this->logDebug("Map entity to output dto for User");
        $span = $this->startSpan("Map entity to output dto for User");
        try {
            $tenant = $value->getTenant();
            $dto = new UserApiDTO();
            $dto->uid = $value->getUid();
            $dto->tenant = $tenant ? ['$ref' => $tenant->uid()] : null;
            $dto->name = $value->getName();
            $dto->password = '******';
            $dto->email = $value->getEmail();
            $dto->wellcomeAt = $value->getWellcomeAt()?->format(DateTime::ATOM);
            $dto->enabled = $value->getEnabled();
            $dto->approve = $value->getApprove();
            $dto->temporalPassword = $value->getTemporalPassword();
            $dto->useSecondFactors = $value->getUseSecondFactors();
            $dto->secondFactorSeed = '******';
            $dto->blockedUntil = $value->getBlockedUntil()?->format(DateTime::ATOM);
            $dto->provider = $value->getProvider();
            $dto->version = $value->getVersion();
            return $dto;
        } catch (Throwable $ex) {
            $span->recordException($ex);
            throw $ex;
        } finally {
            $span->end();
        }
    }
}
